import React, {Component} from "react";
import {Modal, ModalBody, ModalFooter, ModalHeader} from "reactstrap";
import {CoinView} from "../ViewModel/coinView";
import {ProductView} from "../ViewModel/productView";


export interface Props{
	isShown: boolean;
	hide: () => void;
	coins: CoinView[];
	product: ProductView;
	getProducts: () => void;
	getCoins: () => void;
}

type State = {}

export class ProductMachine extends Component<Props, State>{
	constructor(props: Props) {
		super(props);
	}

	async buyProduct(id: number){
		await fetch('buyproduct?id=', {
			method: 'POST',
			headers: {
				'Content-Type': 'application/json;charset=utf-8'
			},
			body: JSON.stringify(id)
		});
		this.props.hide();
		await this.props.getProducts();
	}

	async minusCoinCount(id: number){
		await fetch('updatecoinnow?coinId=', {
			method: 'POST',
			headers: {
				'Content-Type': 'application/json;charset=utf-8'
			},
			body: JSON.stringify(id)
		});
		await this.props.getCoins();
	}

	render() {
		return (
			<div>
				<Modal size="lg"
					   aria-labelledby="contained-modal-title-vcenter"
					   isOpen={this.props.isShown}
					   fade={false}
				>
					<ModalHeader>
						<h1>Покупка {this.props.product.name}</h1>
					</ModalHeader>
					<ModalBody>
						<p>Стоимость: <b>P{this.props.product.price}</b></p>
						<p>Стоимость: P{this.props.product.description}</p>
						
					</ModalBody>
					<ModalFooter>
						<button className="btn btn-success">Купить продукт</button>
						<button className="btn btn-danger">Выйти</button>
					</ModalFooter>
				</Modal>
			</div>
		);
	}
}
