import { Component } from 'react';
import * as React from "react";
import "./NavMenu.css"
import {Mapper} from "../Mapper/Mapper";
import {ProductView} from "../ViewModel/productView";

type Props ={}

type State = {
  products: ProductView[],
  showModal: boolean
}

export class FetchData extends Component<Props, State> {

  constructor(props: Props) {
    super(props);
    
    this.state ={
      products: [],
      showModal: false
    }
  }
  
  componentDidMount() {
    this.getProducts();
  }

  async getProducts(){
    const response = await fetch('getproducts');
    const request = await response.json();
    const products = Mapper.mapToProductViews(request)
    this.setState({products: products})
  }

  async getProduct(id: number){
    const response = await fetch('getproducts/'+ id);
    const request = await response.json();
    const products = Mapper.mapToProductViews(request)
    this.setState({products: products})
  }

  openModal = () =>{
    this.setState({  showModal: true});
  }

  closeModal = () =>{
    this.setState({  showModal: false});
  }
  
  render() {
    return (
      <div>
        <div className="card-columns">
          {this.state.products.map(product =>
              <div id={product.id.toString()} className="card">
                <h1>{product.name}</h1>
                <p className="price">P{product.price}</p>
                <p>{product.description}</p>
                <p>
                  <button>Купить</button>
                </p>
              </div>
          )}
        </div>
      </div>
    );
  }
}
